// Helper to get total chapters in extended OT books
export const getExtendedTotalChapters = () => extendedOTBooks.reduce((sum, book) => sum + book.totalChapters, 0);

// Helper to get total chapters in NT books
export const getNTTotalChapters = () => extendedNTBooks.reduce((sum, book) => sum + book.totalChapters, 0);

// Combined complete Bible (extended OT + NT)
export const completeBibleBooks: BookData[] = [...extendedOTBooks, ...extendedNTBooks];

// Helper to get total chapters in complete Bible (OT extended + NT)
export const getCompleteBibleTotalChapters = () => completeBibleBooks.reduce((sum, book) => sum + book.totalChapters, 0);
